mapper.select:
SELECT FsBoard.id ,FsBoard.title ,FsBoard.content ,FsBoard.deleted ,FsBoard.created_at ,FsBoard.modified_at FROM FsBoard WHERE FsBoard.id is not null AND FsBoard.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= FsBoard.created_at AND FsBoard.title LIKE CONCAT('%', #{title}, '%') AND FsBoard.deleted = #{deleted}
AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > FsBoard.created_at ORDER BY FsBoard.created_at DESC AND FsBoard.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY FsBoard.created_at ASC
LIMIT 0, #{permore}
