mapper.select:
SELECT FsCmt.id ,FsCmt.fs_board_id ,FsCmt.content ,FsCmt.deleted ,FsCmt.created_at ,FsCmt.modified_at FROM FsCmt WHERE FsCmt.id is not null AND FsCmt.created_at >= CONCAT(#{sdate}, ' 00:00:00.000000') AND CONCAT(#{fdate}, ' 23:59:59.999999') >= FsCmt.created_at AND FsCmt.fs_board_id = #{fs_board_id} AND FsCmt.content LIKE CONCAT('%', #{content}, '%') AND FsCmt.deleted = #{deleted}
AND STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') > FsCmt.created_at ORDER BY FsCmt.created_at DESC AND FsCmt.created_at > STR_TO_DATE(#{cdatetime}, '%Y-%m-%d %H:%i:%s.%f') ORDER BY FsCmt.created_at ASC
LIMIT 0, #{permore}
